pragma solidity 0.6.12;

contract Token {
    mapping(address => uint32) balances;

    function mint(uint32 tokens) external {
        require(tokens > 0);
        balances[msg.sender] += tokens;
    }

    function transfer(uint32 value, address to) external {
        // test that not transferring to zero address
        require(to != address(0));
        // check that the value being sent is less than or equal to whats owned by msg.sender
        require(value <= balances[msg.sender]);
        // check for overflow
        require(value + balances[to] > balances[to]);

        balances[msg.sender] -= value;
        balances[to] += value;
    }

    function balanceOf(address addr) public view returns (uint32) {
        return balances[addr];
    }
}
